{
    "nginx/conf.d/default.conf": {
        "parser": "file",
        "find": {
            "    listen": "    listen {{server.build.default.port}};",
            "    #access_log": "    {{env.CONSOLE_ACCESS_LOG}}access_log /home/container/naccess.log;",
            "    access_log": "    {{env.CONSOLE_ACCESS_LOG}}access_log /home/container/naccess.log;",
            "    #error_log": "    {{env.CONSOLE_ERROR_LOG}}error_log  /home/container/nerror.log error;",
            "    error_log": "    {{env.CONSOLE_ERROR_LOG}}error_log  /home/container/nerror.log error;",
            "    set_real_ip_from": "    set_real_ip_from {{env.REVERSE_PROXY_IP}};"
        }
    },
    "php/php.ini": {
        "parser": "file",
        "find": {
            "memory_limit =": "memory_limit = {{env.MEMORY_LIMIT}};"
        }
    }
}
